import React from 'react';
import './index.scss';

function App() {
  const [count, setCount] = React.useState(0);
  const Plus = () =>{
    setCount (count+1);
  }
  const Minus = () =>{
    setCount (count-1);
  }
  return(
    <div className='counter'>
      <h1>Счётчик</h1>
      <h2>{count}</h2>
      <button onClick={Minus}>-</button> 
      <button className ='btnPlus' onClick={Plus}>+</button>
    </div>
  );
}

export default App;
